import React, {useState} from 'react';
import './Intro.css';
import AddDestinationForm from './AddDestinationForm';
import Card from './Card';

function Intro() {
  const [showForm, setShowForm] = useState(false);
  const [destinations, setDestinations] = useState([]);

  const handleAddDestination = (destination) => {
    setDestinations([...destinations, destination]);
    setShowForm(false); // Hide the form after submission
  };


  return (
    <div className="intro-container">
      <div className="intro-content">
        <h2>Track your travel destinations with ease and style!</h2>
        <div className="buttons-container">
          <button className="btn mr-3" onClick={() => setShowForm(true)}>Add destination <span role="img" aria-label="plus">+</span></button>
          <div className="dropdown">
            <button className="btn dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
              Sort by 
            </button>
            <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
              <a className="dropdown-item" href="#">All</a>
              <a className="dropdown-item" href="#">Visited</a>
              <a className="dropdown-item" href="#">Plan to Visit</a>
            </div>
          </div>
        </div>
      </div>
      {showForm && <AddDestinationForm onSubmit={handleAddDestination} />}
      <div className="container">
        <div className="row">
          {destinations.map((destination, index) => (
            <div className="col-md-6" key={index}>
              <Card
                title={destination.title}
                status="Plan to Visit" // You can customize this based on your logic
                description={destination.description}
                rating={destination.rating}
                imageUrl={URL.createObjectURL(destination.image)}
              />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Intro;
