import React, { useState, useEffect } from "react";
import "./index.css";
import Logo from "./components/Logo";
import Header from "./components/Header";
import Locations from "./components/Locations";
import LocationForm from "./components/LocationForm";
import { ThemeProvider, useTheme } from "./ThemeContext";
import ToggleButton from "./components/ToggleButton";

function App() {
  const [showForm, setShowForm] = useState(false);
  const [locations, setLocations] = useState([]);

 // Load theme from localStorage or default to 'light'
 const initialTheme = localStorage.getItem("appTheme") || "light";
 const [theme, setTheme] = useState(initialTheme);

 // Load locations from localStorage or default to empty array
 useEffect(() => {
   const storedLocations = JSON.parse(localStorage.getItem("locations"));
   if (storedLocations) {
     setLocations(storedLocations);
   }
 }, []); // Only run once on component mount

 // Update theme in localStorage
 useEffect(() => {
   localStorage.setItem("appTheme", theme);
 }, [theme]);

 // Update locations in localStorage
 useEffect(() => {
   localStorage.setItem("locations", JSON.stringify(locations));
 }, [locations]); // Update whenever locations change

  return (
    <ThemeProvider>
        <Logo />
        <Header onShowForm={() => setShowForm(true)} />
        {showForm && (
          <LocationForm
            onSubmit={handleAddLocation}
            onClose={() => setShowForm(false)}
          />
        )}
        <Locations locations={locations} />
        <ToggleButton />
    </ThemeProvider>
  );
}

export default App;
